import React, { useState } from "react";
import './SubmitButton.css';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip } from "recharts";

const SubmitButton = () => {
  const [clicked, setClicked] = useState(false);
  const [content, setContent] = useState("");
  const [wordCount, setWordCount] = useState([]);

  const handleClick = () => {
    setClicked(true);
    fetch("https://www.terriblytinytales.com/test.txt")
      .then((response) => response.text())
      .then((data) => {
        setContent(data);
        const words = data.toLowerCase().match(/\b\S+\b/g);
        const wordCountObj = {};
        words.forEach((word) => {
          wordCountObj[word] = (wordCountObj[word] || 0) + 1;
        });
        const wordCountArr = Object.keys(wordCountObj).map((key) => ({
          word: key,
          count: wordCountObj[key],
        }));
        wordCountArr.sort((a, b) => b.count - a.count);
        setWordCount(wordCountArr.slice(0, 20));
      })
      .catch((error) => console.log(error));
  };

  return clicked ? (
    <div style={{ width: "100%", height: 500 }}>
      <BarChart data={wordCount}>
        <XAxis dataKey="word" />
        <YAxis />
        <CartesianGrid strokeDasharray="3 3" />
        <Tooltip />
        <Bar dataKey="count" fill="#8884d8" />
      </BarChart>
    </div>
  ) : (
    <button onClick={handleClick}>Submit</button>
  );
};

export default SubmitButton;
